<template>  <div>    <div class="module-wrapper">      <p class="side_title" v-show="list.length > 1">        <span v-for="(obj, index) in list" @click="breadGo(obj.path)">{{obj.name}}</span>      </p>    </div>  </div></template><script>    import '../../assets/styles/asideMenu_title/index.less';    // import {mapActions,mapGetters, mapState, mapMutations} from 'vuex'    export default {     components:{     },     data(){         return {         }     },     computed: {         matched() {             return this.$route.matched         },         list () {             let bread = [];             for (let obj of this.$route.matched) {                 if (obj.path === '/system') {                     bread.push ({name: '订单统计', path: '/statistics'})                 } else if (obj.path === '/system/addminiapp') {                     bread.push ({name: '品牌管理', path: '/statistics/brandManage'})                 } else if (obj.path === '/system/') {                     bread.push ({name: '漏斗分析', path: '/statistics/funnelAnalysis'})                 }             }         },     },     watch: {},     methods: {         breadGo (path) {             this.goto (path)         }     },     mounted () {     }  }</script>